import React = require('react') 
import Dashboard from './Dashboard'
import * as Header from './Header'
import * as SideBar from './SideBar'
import QuickSideBar from './QuickSideBar'
import PageSpinner from './PageSpinner'
import Footer from './Footer'
import {WaitCover} from "SF/components/utils/WaitCover";
import {ModalContainer} from "SF/components/utils/Modal";

export interface ApplicationProperty {
    children?: JSX.Element[];
}

const SideBarFunc: any = SideBar;

class Application extends React.Component<ApplicationProperty, any>{
    constructor(props: ApplicationProperty) {
        super(props);
    }
    static contextTypes = {
        router: React.PropTypes.object.isRequired
    };
    
    

    render() {
        var header = this.props.children.filter(i =>i && i.type == Header.Container);
        var sidebar = this.props.children.filter(i => i && i.type == SideBar.Container);
        var quicksidebar = this.props.children.filter(i => i &&i.type == QuickSideBar);
        var footer = this.props.children.filter(i => i &&i.type == Footer);
        var views = this.props.children.filter(i => i &&i.type != Footer && i.type != Header.Container && i.type != SideBar.Container && i.type != QuickSideBar);

        return <div>
            <PageSpinner visible={false} />

            <div className="page-header navbar navbar-fixed-top">
                {header}
            </div>
            <div className="clearfix"> </div>
            <div className="page-container">
                <div className="page-sidebar-wrapper" style={{ height: "100%" }}>
                    {sidebar}
                </div>

                <div className="page-content-wrapper">
                    <div className="page-content">
                        <div className="fade-in-up">{views}</div>
                    </div>
                </div>
                <a href="javascript:;" className="page-quick-sidebar-toggler">
                    <i className="icon-login"></i>
                </a>
                <div className="page-quick-sidebar-wrapper">
                    {QuickSideBar}
                </div>
            </div>
            <div className="page-footer">
                {footer}
                <div className="scroll-to-top">
                    <i className="icon-arrow-up"></i>
                </div>
            </div>
            <WaitCover/>
            <ModalContainer/>
       </div>
    }
}

export default Application;