import * as React from 'react';
import {browserHistory} from "react-router";
import {EntityEditor} from "SF/components/webapi/EntityEditor";
import * as Page from "SF/webadmin/components/Page"
export interface EditViewArgs{
    links: Page.IHeadLink[];
    controller: string;
    serviceId?: number;
    loadAction ?:string;
    createAction ?: string;
    updateAction ?:string;
    readonly?: boolean;
    id?: any;
    jumpback?: boolean;
} 
export default function EditView(args: EditViewArgs) {
    return class EditView extends React.Component<{ params: any }, {}>{
        render() {
            var id = args.id;
            if (!id) {
                id = this.props.params && this.props.params.splat && this.props.params.splat.split('/') || null;
                if (id && id.length == 1)
                    id = id[0];
            }
            return <Page.Container>
                <Page.Content>
                    <EntityEditor
                        id={id}
                        controller={args.controller}
                        serviceId={args.serviceId}
                        loadAction={args.loadAction}
                        createAction={args.createAction}
                        updateAction={args.updateAction}
                        readonly={args.readonly}
                        onSubmitSuccess={() => {
                            if (args.jumpback)
                                browserHistory.goBack();
                        }}
                        onBuildSubmitPanel={(props, cmds) =>
                            <Page.Header links={args.links}>
                                {cmds}
                            </Page.Header>
                        }/>
                </Page.Content>
            </Page.Container>
        }
    }
    
}
